
public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a =10;
		int b =20;
		
		
		System.out.println("Before Swapping");
		System.out.println("a is="+a);
		System.out.println("B is="+b);
		a =a+b;
		a =a-b;
		System.out.println("a is =" + a);
	
	System.out.println(b =a);
		
		
		
		
		
	}

}
